mapper.select:
select wxid, openid, name, studentid, dormitory, phone
from user
where wxid = #{wxid}

mapper.update:
update user
set name = #{name,jdbcType=VARCHAR},
  studentid = #{studentid,jdbcType=CHAR},
  dormitory = #{dormitory,jdbcType=VARCHAR},
  phone = #{phone,jdbcType=VARCHAR}
where wxid = #{wxid,jdbcType=INTEGER}
